INSERT INTO `episodes` (`id`, `show_id`, `lookup_episode_status_id`, `title`, `slug`, `content`, `excerpt`, `meta_description`, `canonical_url`, `featured_image`, `start_time`, `end_time`, `recording_date`, `publish_date`, `show_notes`, `research_notes`, `enabled`, `podcast_file_name`, `created_at`, `created_by`, `updated_at`, `updated_by`, `locked_at`, `locked_by`) VALUES
(1, 1, 3, 'LaSalleMart #1: Welcome To The LaSalleMart Podcasts', 'lasallemart1', '', 'Welcome to the LaSalleMart News and Hangout Podcasts. Bringing you updates, and private conversations, about LaSalleMart...', 'Welcome to the LaSalleMart News and Hangout Podcasts. Bringing you updates, and private conversations, about LaSalleMart', 'https://lasallesoftware.ca/lasallemart1', '', '', '', '2012-01-17', '2012-01-17', '', '', 1, 'lasallemart1.mp3', '2012-01-17 19:36:47', 1, '2015-08-18 22:55:10', 1, NULL, NULL),
(2, 1, 3, 'LaSalleMart #2: The Response To &#34;It&#39;s Free Open Source Software&#34;', 'lasallemart2', '', 'Earlier today, we recorded our inaugural LaSalleMart Hangout podcast. Before I publish our first Hangout, background is needed. There&#39;s a philosophy behind my Club Commerce and LaSalleMart...', 'Earlier today, we recorded our inaugural LaSalleMart Hangout podcast. Before I publish our first Hangout, background is needed. There&#39;s a philosophy behind my Club Commerce and LaSalleMart...', 'https://lasallesoftware.ca/lasallemart2', '', '', '', '2012-01-18', '2012-01-18', '', '', 1, 'lasallemart2.mp3', '2012-01-18 19:58:21', 1, '2015-08-18 22:57:47', 1, NULL, NULL),
(4, 1, 3, 'LaSalleMart #3: Inaugural LaSalleMart Hangout Podcast', 'lasallemart3', '<p><span style=\"font-family:georgia,times new roman,bitstream charter,times,serif; font-size:16px\">Zach Atkinson (</span><a href=\"http://machineandmagic.com/\" style=\"font-family: Georgia, \'Times New Roman\', \'Bitstream Charter\', Times, serif; font-size: 16px; line-height: 24px;\" target=\"_blank\" title=\"Machine and Magic\">MachineAndMagic.com</a><span style=\"font-family:georgia,times new roman,bitstream charter,times,serif; font-size:16px\">), Joe Sonne (</span><a href=\"http://joejoomla.com/\" style=\"font-family: Georgia, \'Times New Roman\', \'Bitstream Charter\', Times, serif; font-size: 16px; line-height: 24px;\" target=\"_blank\" title=\"JoeJoomla.com\">JoeJoomla.com</a><span style=\"font-family:georgia,times new roman,bitstream charter,times,serif; font-size:16px\">), and Suzan Marie Chin (</span><a href=\"http://creativeraven.com/\" style=\"font-family: Georgia, \'Times New Roman\', \'Bitstream Charter\', Times, serif; font-size: 16px; line-height: 24px;\" target=\"_blank\" title=\"CreativeRaven.com\">CreativeRaven.com</a><span style=\"font-family:georgia,times new roman,bitstream charter,times,serif; font-size:16px\">) join me today to talk about the philosophy behind LaSalleMart, creating simultaneous builds, the reseller idea, funding, and other topics.</span></p>', 'Zach Atkinson (MachineAndMagic.com), Joe Sonne (JoeJoomla.com), and Suzan Marie Chin (CreativeRaven.com) join me today to talk about the philosophy behind LaSalleMart, creating simultaneous builds, the reseller idea, funding, and other topics.', 'Zach Atkinson (MachineAndMagic.com), Joe Sonne (JoeJoomla.com), and Suzan Marie Chin (CreativeRaven.com) join me today to talk about the philosophy behind LaSalleMart, creating simultaneous builds, the reseller idea, funding, and other topics.', 'https://lasallesoftware.ca/lasallemart3', '', '', '', '2012-01-19', '2012-01-19', '', '', 1, 'lasallemart3.mp3', '2012-01-19 10:00:00', 1, '2015-08-19 07:37:08', 1, NULL, NULL),
(6, 1, 3, 'LaSalleMart #4: The Moneyball Of Joomla ECommerce', 'lasallemart4', '', 'The Moneyball movie is an interesting backdrop for telling you about how I want to approach LaSalleMart&#39;s development....', 'LaSalleMart&#39;s development.', 'https://lasallesoftware.ca/lasallemart4', '', '', '', '2012-02-09', '2012-02-09', '', '', 1, 'lasallemart4.mp3', '2012-02-09 10:00:00', 1, '2015-08-18 23:12:17', 1, NULL, NULL),
(7, 1, 3, 'LaSalleMart #5: Moneyball And The Soul Of Value', 'lasallemart5', '', 'Written summary of my Hangout, as technical problems derailed its publication...', 'Written summary of my Hangout, as technical problems derailed its publication.', 'https://lasallesoftware.ca/lasallemart5', '', '', '', '2012-02-28', '2012-02-28', '', '', 1, 'lasallemart5.mp3', '2012-02-28 10:00:00', 1, '2015-08-18 23:15:01', 1, NULL, NULL),
(8, 1, 3, 'LaSalleMart #6: Continuous Development – You Need To Get A Piece Of This Action', 'lasallemart6', '', '... he gets features to his site faster using &#34;Continuous&#34; ideas. This is, ultimately, the message that is getting out to the programming community, and we should be taking note...', 'He gets features to his site faster using &#34;Continuous&#34; ideas. This is, ultimately, the message that is getting out to the programming community, and we should be taking note.', 'https://lasallesoftware.ca/lasallemart6', '', '', '', '2012-03-27', '2012-03-27', '', '', 1, 'lasallemart6.mp3', '2012-03-27 08:00:00', 1, '2015-08-18 23:17:07', 1, NULL, NULL),
(9, 1, 3, 'LaSalleMart #7: LaSalleMart 1.0 Is A Reality', 'lasallemart7', '', 'My vision is nothing more than a pissed-off angry consultant who is told over and over again by extension purveyors to change the code myself. Well, I did. Not only did I change the code myself, but I did it with my distinct user perspective. Having sat down and actually translated my vision into a tangible and concrete cart extension, and succeeded -- well, it blows my mind...', 'My vision is nothing more than a pissed-off angry consultant who is told over and over again by extension purveyors to change the code myself. Well, I did.', 'https://lasallesoftware.ca/lasallemart7', '', '', '', '2012-06-07', '2012-06-07', '', '', 1, 'lasallemart7.mp3', '2012-06-07 08:00:00', 1, '2015-08-18 23:21:16', 1, NULL, NULL),
(10, 1, 3, 'LaSalleMart #8: LaSalleMart News Video', 'lasallemart8', '<p>Regular monthly update about LaSalleMart and Club, this time via a video.<iframe frameborder=\"0\" height=\"262\" src=\"http://www.youtube.com/embed/X-Y2JODFd94\" width=\"350\"></iframe></p>', 'Regular monthly update about LaSalleMart and Club, this time via a video.', 'Regular monthly update about LaSalleMart and Club, this time via a video.', 'https://lasallesoftware.ca/lasallemart8', '', '', '', '2012-08-05', '2012-08-05', '', '', 1, 'lasallemart8.mp3', '2012-08-05 08:00:00', 1, '2015-08-19 17:26:00', 1, NULL, NULL),
(11, 1, 3, 'LaSalleMart #9: LaSalleMart News', 'lasallemart9', '', 'More on software development, Moneyball of software, and LaSalleMart&#39;s progress.', 'More on software development, Moneyball of software, and LaSalleMart&#39;s progress.', 'https://lasallesoftware.ca/lasallemart9', '', '', '', '2012-09-19', '2012-09-19', '', '', 1, 'lasallemart9.mp3', '2012-09-19 08:00:00', 1, '2015-08-18 23:25:00', 1, NULL, NULL),
(12, 1, 3, 'LaSalleMart #10: Announcing LaSalleMart For Joomla 2.5', 'lasallemart10', '', 'LaSalleMart 2.5 is a fork of the nascent Tienda for Joomla 2.5. Continuous deployment and multiple packages are key goals of LaSalleMart.  To set the table, I busted up the Tienda zip file into 61 separate installable zip files. There is an extension zip, a library zip, a system plugin for the library, 19 modules, and 39 plugins. Each has its own repository. There are 69 build files to put it all together. The only way to install &#39;em is via the Joomla installer. A typical install will only use 10 to 15 zip files, so no need to worry about installing 61 separate zips...', 'LaSalleMart 2.5 is a fork of the nascent Tienda for Joomla 2.5.', 'https://lasallesoftware.ca/lasallemart10', '', '', '', '2012-10-15', '2012-10-15', '', '', 1, 'lasallemart10.mp3', '2012-10-15 08:00:00', 1, '2015-08-18 23:28:35', 1, NULL, NULL),
(13, 1, 3, 'LaSalleMart #11: Joomla-LaSalleMart Package A Reality', 'lasallemart11', '', 'Cover new LaSalleMart website, available packages, Joomla distro, quick hits, and what development is on-deck...', 'Cover new LaSalleMart website, available packages, Joomla distro, quick hits, and what development is on-deck.', 'https://lasallesoftware.ca/lasallemart11', '', '', '', '2012-11-09', '2012-11-09', '', '', 1, 'lasallemart11.mp3', '2012-11-09 10:00:00', 1, '2015-08-18 23:32:19', 1, NULL, NULL),
(14, 1, 3, 'LaSalleMart #12: Re-Launch, Plus New LaSalleCMS', 'lasallemart12', '', 'A month ago, I completed my LaSalleMart 2.0 launch. Having done so, I took a deep relaxing breath and looked at my handiwork. It didn&#39;t take long to conclude that I had more work to do. Most glaring was the lack of a LaSalleMart website. Wanting to direct everyone to my SouthLaSalleCommerce site, all that I accomplished was to confuse myself. My sites were not intuitive to follow...', 'A month ago, I completed my LaSalleMart 2.0 launch. Having done so, I took a deep relaxing breath and looked at my handiwork. It didn&#39;t take long to conclude that I had more work to do. Most glaring was the lack of a LaSalleMart website. Wanting to di', 'https://lasallesoftware.ca/lasallemart12', '', '', '', '2012-12-11', '2012-12-11', '', '', 1, 'lasallemart12.mp3', '2012-12-11 10:00:00', 1, '2015-08-18 23:34:58', 1, NULL, NULL),
(15, 1, 3, 'LaSalleMart #13: New Year Update', 'lasallemart13', '', 'LaSalleMart 2.0 is at a delicious point now. The forking exercise is done. The builds are done. The websites are done...', 'LaSalleMart 2.0 is at a delicious point now. The forking exercise is done. The builds are done. The websites are done.', 'https://lasallesoftware.ca/lasallemart13', '', '', '', '2013-01-07', '2013-01-07', '', '', 1, 'lasallemart13.mp3', '2013-01-07 10:00:00', 1, '2015-08-18 23:41:52', 1, NULL, NULL),
(16, 1, 3, 'LaSalleMart #14: Real World Intrudes', 'lasallemart14', '', 'Your demand for LaSalleMart 2.0 is getting ahead of my user testing debugging and checkout improvements...', 'Your demand for LaSalleMart 2.0 is getting ahead of my user testing debugging and checkout improvements.', 'https://lasallesoftware.ca/lasallemart14', '', '', '', '2013-02-18', '2013-02-18', '', '', 1, 'lasallemart14.mp3', '2013-02-18 10:00:00', 1, '2015-08-19 00:01:25', 1, NULL, NULL),
(17, 1, 3, 'LaSalleMart #15: Ready For Your Site', 'lasallemart15', '', 'There was the initial launch on New Years when I released LaSalleMart 2 for user testing. That initial user testing went well, as problems were discovered. The order details form in admin was broken, checkout was  not ready and had to be revamped, the default date format was messed up, and there were a bunch of little things that were cleaned up. PayPal Standard was tested and passed -- to my relief...', 'There was the initial launch on New Years when I released LaSalleMart 2 for user testing. That initial user testing went well, as problems were discovered.', 'https://lasallesoftware.ca/lasallemart15', '', '', '', '2013-03-05', '2013-03-05', '', '', 1, 'lasallemart15.mp3', '2013-03-05 10:00:00', 1, '2015-08-19 00:04:12', 1, NULL, NULL),
(18, 1, 3, 'LaSalleMart #16: JED, Jump, And Roll', 'lasallemart16', '', 'Updates on LaSalleMart, JED, client tricks...', 'Updates on LaSalleMart, JED, client tricks.', 'https://lasallesoftware.ca/lasallemart16', '', '', '', '2013-04-03', '2013-04-03', '', '', 1, 'lasallemart16.mp3', '2013-04-03 08:00:00', 1, '2015-08-19 00:11:25', 1, NULL, NULL),
(19, 1, 3, 'LaSalleMart #17: Real. Live. LaSalleMart.', 'lasallemart17', '', 'LaSalleCMS distro was ignored last month. LaSalleMart development is just too intense right now. In April the new Knowledge Base sprouted, there were six builds, several core areas received a makeover, and I&#39;m up to 41 issues in my issue tracker already...', 'LaSalleCMS distro was ignored last month. LaSalleMart development is just too intense right now. In April the new Knowledge Base sprouted, there were six builds, several core areas received a makeover, and I&#39;m up to 41 issues in my issue tracker alrea', 'https://lasallesoftware.ca/lasallemart17', '', '', '', '2013-05-02', '2013-05-02', '', '', 1, 'lasallemart17.mp3', '2013-05-02 08:00:00', 1, '2015-08-19 00:20:04', 1, NULL, NULL),
(20, 1, 3, 'LaSalleMart #18: JED, JUGT, And Roll Redux', 'lasallemart18', '', 'Installs, JED, JUGT presentation, Demo, Mandrill, Framework on Framework.', 'Installs, JED, JUGT presentation, Demo, Mandrill, Framework on Framework.', 'https://lasallesoftware.ca/lasallemart18', '', '', '', '2013-06-05', '2013-06-05', '', '', 1, 'lasallemart18.mp3', '2013-06-05 08:00:00', 1, '2015-08-19 00:27:20', 1, NULL, NULL),
(22, 1, 3, 'LaSalleMart #20: Nyet To JED', 'lasallemart20', '', 'The JED listing process has been very educational...', 'The JED listing process has been very educational.', 'https://lasallesoftware.ca/lasallemart20', '', '', '', '2013-08-01', '2013-08-01', '', '', 1, 'lasallemart20.mp3', '2013-08-01 08:00:00', 1, '2015-08-19 00:38:30', 1, NULL, NULL),
(23, 1, 3, 'LaSalleMart #21: LaSalleMart 2 On Ice', 'lasallemart21', '', 'After a longer than expected journey, LaSalleMart 2 is done. A wonderful base Joomla ecommerce package, with a nice little knowledge base to supplement its administration screens. LaSalleMart 2 is now at the point where it&#39;s time to broaden its functionality, start refactoring the code, and move it to Joomla 3. Exactly where I want it to be. Except that I&#39;ve concluded that it is better to start fresh than to build out LM2. A startling conclusion!', 'I&#39;ve concluded that it is better to start fresh than to build out LM2. A startling conclusion!', 'https://lasallesoftware.ca/lasallemart21', '', '', '', '2013-09-01', '2013-09-01', '', '', 1, 'lasallemart21.mp3', '2013-09-01 08:00:00', 1, '2015-08-19 00:50:41', 1, NULL, NULL),
(24, 1, 3, 'LaSalleMart #22: LaSalleMart 3', 'lasallemart22', '', 'No forking around! LaSalleMart 3 is from-scratch based on the superb Akeeba Framework on Framework.', 'No forking around! LaSalleMart 3 is from-scratch based on the superb Akeeba Framework on Framework.', 'https://lasallesoftware.ca/lasallemart22', '', '', '', '2013-10-01', '2013-10-01', '', '', 1, 'lasallemart22.mp3', '2013-10-01 08:00:00', 1, '2015-08-19 01:39:00', 1, NULL, NULL),
(25, 1, 3, 'LaSalleMart #23: Vision Fruition', 'lasallemart23', '', 'Although LaSalleMart 3 development slowed down by client work in October, the second prototyping is going well. I am so concerned about using Joomla and the Framework on Framework in a certain way that I&#39;m still testing my approach. So far, it is going very well...', 'Although LaSalleMart 3 development slowed down by client work in October, the second prototyping is going well. I am so concerned about using Joomla and the Framework on Framework in a certain way that I&#39;m still testing my approach. So far, it is goin', 'https://lasallesoftware.ca/lasallemart23', '', '', '', '2013-11-01', '2013-11-01', '', '', 1, 'lasallemart23.mp3', '2013-11-01 08:00:00', 1, '2015-08-19 01:41:40', 1, NULL, NULL),
(26, 1, 3, 'LaSalleMart #24: LM3 Alpha One Achieved', 'lasallemart24', '', 'It is unusual that I get so excited about non-production, first Alpha, software that is not even available to the public. But, today is that day because the initial Alpha version of LaSalleMart3 is done...', 'It is unusual that I get so excited about non-production, first Alpha, software that is not even available to the public. But, today is that day because the initial Alpha version of LaSalleMart3 is done.', 'https://lasallesoftware.ca/lasallemart24', '', '', '', '2013-12-01', '2013-12-01', '', '', 1, 'lasallemart24.mp3', '2013-12-01 10:00:00', 1, '2015-08-19 01:52:34', 1, NULL, NULL),
(27, 1, 3, 'LaSalleMart #25: Demo, Show And Day', 'lasallemart25', '', 'This month&#39;s podcast is an informal chat about LaSalleMart3&#39;s progress, a renewed &#34;The Bob Bloom Show&#34; podcast, a brand new Value Proposition, thoughts on my Joomla Distro; and, a word about a potential JoomlaDay Toronto this year.', 'This month&#39;s podcast is an informal chat about LaSalleMart3&#39;s progress, a renewed &#34;The Bob Bloom Show&#34; podcast, a brand new Value Proposition, thoughts on my Joomla Distro; and, a word about a potential JoomlaDay Toronto this year.', 'https://lasallesoftware.ca/lasallemart25', '', '', '', '2014-01-03', '2014-01-03', '', '', 1, 'lasallemart25.mp3', '2014-01-03 10:00:00', 1, '2015-08-19 07:10:33', 1, NULL, NULL),
