INSERT INTO `shows` (`id`, `title`, `slug`, `content`, `excerpt`, `meta_description`, `featured_image`, `enabled`, `media_file_storage_url`, `image_file_storage_url`, `created_at`, `created_by`, `updated_at`, `updated_by`, `locked_at`, `locked_by`) VALUES
(1, 'LaSalleMart News', 'lasallemart', '<p>Thirty-seven&nbsp;<em>LaSalleMart News</em>&nbsp;podcasts were published between January 2012 and May 2015, reporting the progress of my LaSalleMart software.</p>', 'Thirty-seven LaSalleMart News podcasts were published between January 2012 and May 2015 to update you on my LaSalleMart software, which broadened into LaSalle Software. My replacement podcast, LaSalle Software News, starts in the fall 2015.', 'Thirty-seven LaSalleMart News podcasts were published between January 2012 and May 2015 to update you on my LaSalleMart software, which broadened into LaSalle Software. My replacement podcast, LaSalle Software News, starts in the fall 2015.', 'http://d3nim47ta4anuc.cloudfront.net/artwork/lasallemartnews-300x300.png', 1, 'http://d3nim47ta4anuc.cloudfront.net/lasallemart', 'https://lasallesoftware.ca/images_episodes/', '2015-08-18 17:10:23', 1, '2017-01-13 00:44:01', 1, NULL, NULL),
(2, 'The Bob Bloom Show', 'thebobbloomshow', '<p>Monthly commentary and interviews about websites,&nbsp;technology, and consulting. Produced by Bob Bloom, founder and developer of LaSalle Software.</p>\r\n<p>Produced 57 podcasts from 2010 to 2016.</p><p> Currently on hiatus.</p>', 'Monthly commentary and interviews about websites, technology, and consulting. Produced by Bob Bloom, founder and developer of LaSalle Software.', 'Monthly commentary and interviews about websites, technology, and consulting. Produced by Bob Bloom, founder and developer of LaSalle Software.', 'http://d3nim47ta4anuc.cloudfront.net/artwork/bobbloomshow-300x300.jpg', 1, 'http://d3nim47ta4anuc.cloudfront.net/thebobbloomshow', 'https://lasallesoftware.ca/images_episodes/', '2015-08-20 23:15:13', 1, '2017-01-13 00:43:42', 1, NULL, NULL),
(3, 'LaSalle Software News', 'lasallesoftwarenews', '<p>Monthly report on the good, the bad, and the ugly of my ongoing LaSalle Software development.&nbsp;Produced by Bob Bloom, founder and developer of LaSalle Software.</p>', 'LaSalle Software monthly update podcast. Reporting on the good, the bad, and the ugly of ongoing development. Produced by Bob Bloom, founder and developer of LaSalle Software.', 'LaSalle Software monthly update podcast. Reporting on the good, the bad, and the ugly of ongoing development. Produced by Bob Bloom, founder and developer of LaSalle Software.', 'http://d3nim47ta4anuc.cloudfront.net/artwork/lasallesoftwarenews-1400x1400.png', 1, 'SOUNDCLOUD-LASALLESOFTWARENEWS', 'https://lasallesoftware.ca/images_episodes/', '2015-09-25 04:54:23', 1, '2016-11-21 13:03:58', 1, NULL, NULL);
